mapper.select:
SELECT * FROM exam


        AND exam_name LIKE CONCAT('%', #{examName}, '%')


        AND exam_type = #{examType}


        AND status = #{status}


ORDER BY create_time DESC

SELECT * FROM exam
WHERE status IN (2, 3)
AND registration_start <= #{currentTime}
AND registration_end >= #{currentTime}

    AND exam_name LIKE CONCAT('%', #{examName}, '%')


    AND exam_type = #{examType}

ORDER BY exam_date ASC
